import LectureClass from "./components/LectureClass";

function App() {
  const data = [
    {
      title: "Math",
      amount: 123,
      date: new Date(4,12,2000)
    },
    {
      title: "Science",
      amount: 300,
      date: new Date(4,12,2010)
    },
    {
      title: "Math",
      amount: 123,
      date: new Date(4,12,2020)
    },
  ]
  return (
    <div>
      <h2>Let's get started!</h2>
      <LectureClass
        title={data[0].title} 
        amount = {data[0].amount}
        date = {data[0].date}></LectureClass>
        <LectureClass
        title={data[1].title} 
        amount = {data[1].amount}
        date = {data[1].date}></LectureClass>
        <LectureClass
        title={data[1].title} 
        amount = {data[1].amount}
        date = {data[1].date}></LectureClass>
    </div>
  );
}

export default App;
